// Prepare notification content with acceptance link
const userData = $node["Supabase: Fetch User Details"].json.data;
const userId = $input.all()[0].json.user_id;
const assignedRoleName = $input.all()[0].json.assigned_role_name;
const taskId = $input.all()[0].json.task_id;

if (!userData || userData.length === 0) {
  throw new Error('User not found.');
}

const user = userData[0];

// In a production environment, 'GENERATED_TOKEN_HERE' would be a secure, one-time token
// linked to the task and user, validated by a backend endpoint.
const acceptanceLink = `https://your-eprm-platform.com/accept-role?user_id=${user.id}&role=${assignedRoleName}&task_id=${taskId}&token=GENERATED_TOKEN_HERE`;

const content = `Estimado/a ${user.full_name},\n\nSe te ha asignado el rol de '${assignedRoleName}' en la plataforma EPRM SUITE. Es obligatorio confirmar la aceptación de este rol y sus responsabilidades. Por favor, haz clic en el siguiente enlace para aceptar:\n\n${acceptanceLink}\n\nEste acto se registrará como un acuse digital obligatorio.\n\nSaludos,\nEquipo EPRM`;

return [{
  json: {
    email: user.email,
    full_name: user.full_name,
    notification_content: content,
    user_id: userId,
    assigned_role_name: assignedRoleName,
    task_id: taskId
  }
}];

// Log acceptance request event
const userId = $input.all()[0].json.user_id;
const assignedRoleName = $input.all()[0].json.assigned_role_name;
const taskId = $input.all()[0].json.task_id;
const communicationId = $node["Execute Workflow: [MCP-SUB] A13-SendCommunication (Request Acceptance)"].json.communication_id || 'N/A';

// Prepare log event parameters
const logParams = {
  log_level: 'INFO',
  log_message: `Solicitud de aceptación de rol '${assignedRoleName}' enviada a usuario (ID: ${userId}). Comunicación ID: ${communicationId}`,
  task_id: taskId,
  agent_id: 'A03',
  details: { 
    user_id: userId,
    assigned_role: assignedRoleName,
    communication_id: communicationId
  }
};

return [{
  json: {
    success: true,
    communication_id: communicationId,
    log_params: logParams
  }
}];

// Return final output
return [{
  json: {
    success: $input.all()[0].json.success,
    communication_id: $input.all()[0].json.communication_id
  }
}];
