// Extraer y validar parámetros de entrada
const body = $input.item.json.body || {};
const headers = $input.item.json.headers || {};

// Parámetros requeridos
const { organization_id, creator_user_id, task_type } = body;

// Validación de campos obligatorios
if (!organization_id || !creator_user_id || !task_type) {
  console.log('Validación fallida: Faltan campos requeridos', {
    organization_id: !!organization_id,
    creator_user_id: !!creator_user_id,
    task_type: !!task_type
  });

  return {
    isValid: false,
    error: 'Input validation failed: Missing organization_id, creator_user_id, or task_type',
    missing_fields: {
      organization_id: !organization_id,
      creator_user_id: !creator_user_id,
      task_type: !task_type
    }
  };
}

// Parámetros opcionales con valores por defecto
const parameters = body.parameters || {};
const priority = body.priority || 0;
const project_id = body.project_id || null;

// Validación adicional del UUID format (básica)
const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

if (!uuidRegex.test(organization_id)) {
  return {
    isValid: false,
    error: 'Invalid organization_id format - must be a valid UUID'
  };
}

if (!uuidRegex.test(creator_user_id)) {
  return {
    isValid: false,
    error: 'Invalid creator_user_id format - must be a valid UUID'
  };
}

if (project_id && !uuidRegex.test(project_id)) {
  return {
    isValid: false,
    error: 'Invalid project_id format - must be a valid UUID'
  };
}

// Si todo es válido, estructurar los datos
const structuredData = {
  organization_id,
  creator_user_id,
  task_type,
  parameters,
  priority,
  project_id,
  timestamp: new Date().toISOString(),
  request_source: headers['user-agent'] || 'unknown'
};

console.log('Parámetros validados y estructurados exitosamente:', {
  task_type,
  organization_id,
  creator_user_id,
  priority,
  has_parameters: Object.keys(parameters).length > 0
});

return {
  isValid: true,
  data: structuredData
};

// Procesar resultado de route_task_request
const result = $input.item.json[0];

if (!result) {
  throw new Error('No result returned from route_task_request');
}

const {
  task_id,
  assigned_agent_id,
  agent_capability,
  success,
  error_message
} = result;

// Validar campos críticos
if (!success) {
  throw new Error(`Task routing failed: ${error_message || 'Unknown error'}`);
}

if (!task_id || !assigned_agent_id) {
  throw new Error('Missing critical fields in route_task_request response');
}

// Determinar nombre del subworkflow
let subworkflow_name = '';
if (agent_capability) {
  // Convertir agent_capability a CamelCase para el nombre del subworkflow
  const words = agent_capability.split('_');
  const camelCase = words.map(word => 
    word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
  ).join('');

  subworkflow_name = `[MCP-SUB] ${assigned_agent_id}-${camelCase}`;
} else {
  // Fallback si no hay agent_capability definida
  subworkflow_name = `[MCP-SUB] ${assigned_agent_id}-DefaultTask`;
}

// Obtener parámetros originales para pasarlos al subworkflow
const originalData = $input.first().json.data;
const original_parameters = originalData?.parameters || {};

console.log('Subworkflow determinado:', {
  task_id,
  assigned_agent_id,
  agent_capability,
  subworkflow_name,
  parameter_count: Object.keys(original_parameters).length
});

return {
  task_id,
  assigned_agent_id,
  agent_capability,
  subworkflow_name,
  original_parameters,
  routing_success: true,
  timestamp: new Date().toISOString()
};

