// Extract and normalize chatbot input
const body = $json.body || {};
const message = body.message || body.text || body.query || '';
const userId = body.user_id || body.userId || 'anonymous';
const sessionId = body.session_id || body.sessionId || `session_${Date.now()}`;

// Clean and normalize the message
const cleanMessage = message.toLowerCase().trim();

console.log(`Chatbot input received from user ${userId}: "${message}"`);

return {
  original_message: message,
  clean_message: cleanMessage,
  user_id: userId,
  session_id: sessionId,
  timestamp: new Date().toISOString(),
  message_length: message.length
};

// Parse AI response and extract intent
const aiResponse = $json.message?.content || $json.choices?.[0]?.message?.content || '';
const originalInput = $('Extract Chatbot Input').item.json;

let parsedResponse = {};
try {
  // Try to parse JSON response from AI
  parsedResponse = JSON.parse(aiResponse);
} catch (error) {
  console.error('Failed to parse AI response:', error);
  // Fallback parsing
  parsedResponse = {
    intent: 'unknown',
    test_case: null,
    parameters: null,
    confidence: 0.0,
    response_message: 'No pude entender tu solicitud. ¬øPuedes ser m√°s espec√≠fico?',
    needs_clarification: true
  };
}

// Validate required fields
if (!parsedResponse.intent) parsedResponse.intent = 'unknown';
if (typeof parsedResponse.confidence !== 'number') parsedResponse.confidence = 0.5;
if (!parsedResponse.response_message) parsedResponse.response_message = 'Procesando tu solicitud...';

// Add metadata
parsedResponse.original_message = originalInput.original_message;
parsedResponse.user_id = originalInput.user_id;
parsedResponse.session_id = originalInput.session_id;
parsedResponse.timestamp = new Date().toISOString();

console.log(`AI Intent parsed: ${parsedResponse.intent} (confidence: ${parsedResponse.confidence})`);
if (parsedResponse.test_case) {
  console.log(`Test case identified: ${parsedResponse.test_case}`);
}

return parsedResponse;

